import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [token, setToken] = useState('');
  const [tasks, setTasks] = useState([]);
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [mcqs, setMcqs] = useState([]);
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  // Simple login for student (mock)
  const login = async () => {
    // Replace with real login API call
    const response = await axios.post('http://localhost:3000/auth/login', {
      username: 'student1',
      password: 'password',
    });
    setToken(response.data.access_token);
  };

  // Fetch tasks for student
  const fetchTasks = async () => {
    if (!token) return;
    const response = await axios.get('http://localhost:3000/student/tasks', {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(response.data);
  };

  // Fetch MCQs for selected task
  const fetchMCQs = async (taskId) => {
    setSelectedTaskId(taskId);
    const response = await axios.get(`http://localhost:3000/student/mcqs/${taskId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setMcqs(response.data);
    setAnswers({});
    setResult(null);
  };

  // Handle answer selection
  const handleAnswerChange = (mcqId, optionIndex) => {
    setAnswers({ ...answers, [mcqId]: optionIndex });
  };

  // Submit answers
  const submitAnswers = async () => {
    const payload = {
      taskId: selectedTaskId,
      answers: Object.entries(answers).map(([mcqId, optionIndex]) => ({
        mcqId: parseInt(mcqId),
        selectedOptionIndex: optionIndex,
      })),
    };
    const response = await axios.post('http://localhost:3000/student/submit-answers', payload, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setResult(response.data);
  };

  useEffect(() => {
    if (token) {
      fetchTasks();
    }
  }, [token]);

  if (!token) {
    return (
      <div>
        <h2>Student Login</h2>
        <button onClick={login}>Login as student1</button>
      </div>
    );
  }

  if (!selectedTaskId) {
    return (
      <div>
        <h2>Available Tasks</h2>
        <ul>
          {tasks.map(task => (
            <li key={task.id}>
              {task.title} - <button onClick={() => fetchMCQs(task.id)}>Start Task</button>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  return (
    <div>
      <h2>Task MCQs</h2>
      {mcqs.map(mcq => (
        <div key={mcq.id}>
          <p>{mcq.question}</p>
          {mcq.options.map((option, idx) => (
            <label key={idx}>
              <input
                type="radio"
                name={`mcq-${mcq.id}`}
                checked={answers[mcq.id] === idx}
                onChange={() => handleAnswerChange(mcq.id, idx)}
              />
              {option}
            </label>
          ))}
        </div>
      ))}
      <button onClick={submitAnswers}>Submit Answers</button>
      {result && (
        <div>
          <h3>Result</h3>
          <p>Score: {result.score}</p>
          {result.resultImageUrl && <img src={result.resultImageUrl} alt="Result" />}
        </div>
      )}
    </div>
  );
}

export default App;
