import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [taskId, setTaskId] = useState(1); // example taskId
  const [mcqs, setMcqs] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [resultImage, setResultImage] = useState(null);

  useEffect(() => {
    // Fetch MCQs for the task
    axios.get(`http://localhost:3000/student/mcqs/${taskId}`)
      .then(response => {
        setMcqs(response.data);
        setAnswers(new Array(response.data.length).fill(null));
      })
      .catch(error => {
        console.error('Error fetching MCQs:', error);
      });
  }, [taskId]);

  const handleOptionSelect = (optionIndex) => {
    const newAnswers = [...answers];
    newAnswers[currentQuestionIndex] = optionIndex;
    setAnswers(newAnswers);
  };

  const handleNext = () => {
    if (currentQuestionIndex < mcqs.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handleSubmit = () => {
    // Submit answers to backend
    axios.post('http://localhost:3000/student/submit-answers', {
      taskId,
      studentId: 1, // example studentId, should be dynamic/authenticated
      answers,
    })
    .then(response => {
      setResultImage(response.data.imageUrl);
    })
    .catch(error => {
      console.error('Error submitting answers:', error);
    });
  };

  if (resultImage) {
    return (
      <div>
        <h2>Your Result</h2>
        <img src={resultImage} alt="Result" />
      </div>
    );
  }

  if (mcqs.length === 0) {
    return <div>Loading questions...</div>;
  }

  const currentQuestion = mcqs[currentQuestionIndex];

  return (
    <div>
      <h2>Question {currentQuestionIndex + 1} of {mcqs.length}</h2>
      <p>{currentQuestion.question}</p>
      <ul>
        {currentQuestion.options.map((option, index) => (
          <li key={index}>
            <label>
              <input
                type="radio"
                name="option"
                checked={answers[currentQuestionIndex] === index}
                onChange={() => handleOptionSelect(index)}
              />
              {option}
            </label>
          </li>
        ))}
      </ul>
      {currentQuestionIndex < mcqs.length - 1 ? (
        <button onClick={handleNext} disabled={answers[currentQuestionIndex] === null}>Next</button>
      ) : (
        <button onClick={handleSubmit} disabled={answers[currentQuestionIndex] === null}>Submit</button>
      )}
    </div>
  );
}

export default App;
